import React,{useState, useEffect} from 'react'
import axios from "axios";
import { Link } from 'react-router-dom';

const UserList = () => {
    const [ users, setUser ] = useState([]);

    useEffect(()=> {
    getUsers();
}, []);
//console.log("State Users:", users);

    // const getUsers = async () => {
    //     const response = await axios.get('http://localhost:5000/users');
    //     console.log(response.data);
    // };
    const getUsers = async () => {
    try {
        const response = await axios.get('http://localhost:5000/users');
        console.log(response.data); // Cek apakah data muncul
        setUser(response.data); // Simpan data ke state
    } catch (error) {
        console.error("Error fetching users:", error);
    }
    };
    
    const deleteList = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/${id}`);
            getUsers
        } catch (error) {
            console.log(error);
        }
    };


    return (
  <div className="columns mt-5 is-centered">
            <div className="column is-half">
                <Link to={`add`} className="button is-succes">Add New</Link>
              <table className="table is striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Nama</th>
                        <th>Email</th>
                        <th>Password</th>
                        <th>Action</th>
                    </tr>
                </thead>
                    <tbody>
                        {users.map((user, index) => (
                        <tr key={user.id}> 
                            <td>{index + 1}</td>
                            <td>{ user.nama}</td>
                        <td>{ user.email}</td>
                        <td>{ user.password}</td>
                        <td>
                            <Link to={`edit/${user.id}`} className="button is-samll is-info">edit</Link>
                            <button onClick={()=>deleteList(user.id)} className="button is-samll is-danger">delete</button>
                        </td>
                    </tr>
                       ))}
                </tbody>
              </table>
    </div>
  </div>
  )
}

export default UserList
