import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Link } from 'react-router-dom';

const NotesList = () => {
    const [notes, setNotes] = useState([]);

    useEffect(() => {
        getNotes();
    }, []);

    const getNotes = async () => {
        try {
            const response = await axios.get('http://localhost:5000/notes');
            setNotes(response.data);
        } catch (error) {
            console.error("Error fetching notes:", error);
        }
    };

    const deleteNote = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/notes/${id}`);
            getNotes();
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className="container mx-auto p-5">
            <div className="flex justify-between items-center mb-5">
                <h1 className="text-2xl font-bold">Daftar Catatan</h1>
                <Link to={`add`} className="bg-blue-500 text-white px-4 py-2 rounded">Add New</Link>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-5">
                {notes.map((note) => (
                    <div key={note.id} className="bg-gray-100 p-4 shadow-lg rounded-lg border border-gray-300">
                        <div className="border-b border-gray-400 pb-2 mb-2 flex justify-between items-center">
                            <h2 className="text-lg font-semibold">{note.judul}</h2>
                            <span className="text-sm text-gray-500">Deadline: {new Date(note.deadline).toLocaleDateString()}</span>
                        </div>
                        <p className="text-gray-700 mb-3 line-clamp-3">{note.isi}</p>
                        <div className="border-t border-gray-400 pt-2 flex justify-between text-sm text-gray-600">
                            <span>{note.matakuliah}</span>
                            <span>Dibuat: {new Date(note.createdAt).toLocaleDateString()}</span>
                        </div>
                        <div className="flex justify-end mt-3">
                            <Link to={`edit/${note.id}`} className="bg-green-500 text-white px-3 py-1 rounded mr-2">Edit</Link>
                            <button onClick={() => deleteNote(note.id)} className="bg-red-500 text-white px-3 py-1 rounded">Delete</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default NotesList;
